import React, {useRef, useEffect} from "react";
import styled, {ThemeProvider} from "styled-components";
import greyTriangle from "../misc/images/tr_2.png";
import heroImg from "../misc/images/hero2.png"
import PageWrapper from "../components/PageWrapper";
import StyledTriangle from "../components/Triangles.js";
import HeroImg from "../components/HeroImage";
import Button from "../components/Button";
import TextRectangle from "../components/TextRectangle";
import rootTheme from "../themes/root";
import gitLogo from "../misc/images/github.png";

const HeroPageWrapper = styled(PageWrapper)`
  display: flex;
  justify-content: space-around;
  align-items: center;
  z-index: -2;
`;

const TextWrapper = styled.div`
  display: flex;
  flex-flow: column;
  justify-content: center;
  font-size: ${props => props.theme.fontSize.xxlarge}vw;
`;

const StyledText = styled.h1`
  z-index: 1;
  font-size: ${props => props.theme.fontSize.xxlarge}vw;
  font-weight: bold;
  margin: 0;
`;

const HeroButton = styled(Button)`
margin-top: 1vh;
width: 45%;
height: 10vh;
align-self: flex-end;
font-weight: bold;
font-size: ${props => props.theme.fontSize.buttonFontSize}vw;
`;

const LinksWrapper = styled.div`
    height: 25%;
    display: flex;
    font-size: ${props => props.theme.fontSize.medium}vw;
    font-weight: bold;
    justify-content: space-around;
    align-items: center;
    text-decoration: none;
    color: ${props => props.theme.colors.textColor};
`;

const LinkLogo = styled.img`
    height: 55%;
    margin-right: 1vw;
`;

const HeroPage = () => {

    return(
        <ThemeProvider theme={rootTheme}>
        <HeroPageWrapper>
            <HeroImg as="img" src={heroImg} alt="hero" />
            <StyledTriangle as="img" src={greyTriangle} alt="grey" />
            <TextWrapper>
                <TextRectangle primary>Welcome,</TextRectangle>
                <StyledText>my name is Micha≈Ç</StyledText>
                <LinksWrapper as="a" href={props.href}>
                    <LinkLogo src={gitLogo} alt={"Git"}/>
                    <Button primary>Check the source!</Button>
                </LinksWrapper>
            </TextWrapper>
        </HeroPageWrapper>
        </ThemeProvider>
    )
}

export {HeroPage};
