import React from "react";
import styled from "styled-components";
import greyTriangle from "../misc/images/tr_2.png";
import heroImg from "../misc/images/hero2.png"
import {StyledBlueTriangle} from "../components/Triangles.js";
import {HeroImg} from "../components/HeroImage";
import {Button} from "../components/Button";

const HeroPageWrapper = styled.section`
  width: 100vw;
  height: 100vh;
  background-color: #242526;
  position: relative;
  display: flex;
  justify-content: space-around;
  align-items: center;
  z-index: -2;
`;

const TextWrapper = styled.div`
  display: flex;
  flex-flow: column;
  justify-content: center;
`;

const HeroButton = styled(Button)`
margin-top: 1vh;
width: 55%;
align-self: flex-end;
font-weight: bold;
font-size: 1vw;
`

const StyledText = styled.h1`
  font-size: 4.5vw;
  font-weight: bold;
  margin: 0;
`


function HeroPage() {
    return(
        <HeroPageWrapper>
            <HeroImg as="img" src={heroImg} alt="hero" />
            <StyledBlueTriangle as="img" src={greyTriangle} alt="blue" />
            <TextWrapper>
                <StyledText>Welcome,</StyledText>
                <StyledText>my name is Micha≈Ç</StyledText>
                <HeroButton>You should know me better</HeroButton>
            </TextWrapper>
        </HeroPageWrapper>
    )
}

export {HeroPage};
