import React from "react";
import styled, {ThemeProvider} from "styled-components";
import blueTriangle from "../misc/images/tr_3.png";
import RotatedTriangleImg from "../components/RotatedTriangle";
import PageWrapper from "../components/PageWrapper";
import rootTheme from "../themes/root";
import StyledTriangle from "../components/Triangles";
import {ReactComponent as Laptop} from "../misc/images/laptop.svg";
import Arrow from "../components/Arrow";
import arrowSrc from "../misc/images/nextWhite.svg";

const HireMePageWrapper = styled(PageWrapper)`
  display: flex;
  justify-content: space-around;
  align-items: center;
  z-index: -2;
`;

const Wrapper = styled.div`
display: flex;
justify-content: space-between;
align-items: center;
height: 85%;
`
const StyledLaptop = styled(Laptop)`
    z-index: 3;
    height: 80%;
    width: auto;
`;

const OuterWrapper = styled.div`
  display: flex;
  flex-flow: column;
  justify-content: space-around;
  align-items: center;
  height: 100%;
  width: 100%;
`

const StyledText = styled.h1`
    font-size: ${props => props.theme.fontSize.xlarge}vw;
`;

const ArrowRight = styled(Arrow)`
    width: 10vw;
    height: 10vh;
    z-index: 1;
`;

const ArrowLeft = styled(ArrowRight)`
    transform: rotate(180deg);
`;


const HireMePage = () => {
    return(
        <ThemeProvider theme={rootTheme}>
            <HireMePageWrapper>
                <StyledTriangle as="img" src={blueTriangle} alt="blue" />
                <OuterWrapper>
                    <StyledText>Check my projects!</StyledText>
                    <Wrapper>
                        <ArrowLeft as="img" src={arrowSrc} alt="arrow" />
                        <StyledLaptop />
                        <ArrowRight as="img" src={arrowSrc} alt="arrow" />
                    </Wrapper>
                </OuterWrapper>
            </HireMePageWrapper>
        </ThemeProvider>
    )
}

export {HireMePage};
