import React from "react";
import styled from "styled-components";
import {StyledBlueTriangle} from "../components/Triangles.js";
import greyTriangle from "../misc/images/tr_2.png";
// import {HeroImage} from "../components/HeroImage.js"
import {HeroImg} from "../components/HeroImage";
import heroImg from "../misc/images/hero2.png"

const HeroPageWrapper = styled.section`
  width: 100vw;
  height: 100vh;
  background-color: #242526;
  position: relative;
  z-index: -2;
`;

const TriangleWrapper = styled.div`
    height: 100vh;
    width: 100vw;
    position: absolute;
    top: 0;
    left: 0;
    z-index: -1;
`;

function HeroPage() {
    return(
        <HeroPageWrapper>
            <HeroImg as="img" src={heroImg} alt="hero" />
            {/*<TriangleWrapper>*/}
                <StyledBlueTriangle as="img" src={greyTriangle} alt="blue" />
            {/*</TriangleWrapper>*/}
        </HeroPageWrapper>

    )
}

export {HeroPage};
