import React from "react";
import styled from "styled-components";
import StyledTriangle from "./Triangles";
import gitLogo from "../misc/images/github.png";
import Button from "./Button";

const ProjectWrapper = styled.div`
  width: 75vw;
  height: 65vh;
  background-color: ${props => props.theme.colors.accentBackground};
  z-index: 1;
  display: flex;
  box-shadow: 2px 2px 2px 1px #181b21;
`;

const Logo = styled.img`
  margin: .5vw;
  height: 100%;
  opacity: .9;
`;

const UsedTechnologies = styled.div`
height: 25%;
display: flex;
justify-content: space-around;
align-items: center;
font-size: ${props => props.theme.fontSize.large}vw;
font-weight: bold;
margin-left: 2.5vw;
`;

const LinksWrapper = styled.div`
height: 25%;
display: flex;
font-size: ${props => props.theme.fontSize.medium}vw;
font-weight: bold;
justify-self: flex-end;
align-self: flex-end;
justify-content: space-around;
align-items: center;
text-decoration: none;
color: ${props => props.theme.colors.textColor};
`;

const LinkLogo = styled.img`
height: 55%;
margin-right: 1vw;
`;

const Wrapper = styled.div`
margin-top: .5vh;
display: flex;
height: 25%;
justify-content: center;
align-items: center;
`;


const Project = (props) => {
    return (<ProjectWrapper>
            <Wrapper>
                <Logo src={props.logo}/>
                <UsedTechnologies>{props.technologies} project</UsedTechnologies>
            </Wrapper>
            <LinksWrapper as="a" href={props.href}>
                <LinkLogo src={gitLogo} alt={"Git"}/>
                <Button primary>Check the source!</Button>
            </LinksWrapper>
        </ProjectWrapper>
    )
};

export default Project
