import React from "react";
import styled from "styled-components";
import greyTriangle from "../misc/images/tr_2.png";
import heroImg from "../misc/images/hero2.png"
import {StyledBlueTriangle} from "../components/Triangles.js";
import {HeroImg} from "../components/HeroImage";
import {Button} from "../components/Button";

const HeroPageWrapper = styled.section`
  width: 100vw;
  height: 100vh;
  background-color: #242526;
  position: relative;
  display: flex;
  justify-content: space-around;
  z-index: -2;
`;

const TextWrapper = styled.div`
  
`;

const StyledText = styled.h1`
  font-size: xx-large;
  font-weight: bold;
`


function HeroPage() {
    return(
        <HeroPageWrapper>
            <HeroImg as="img" src={heroImg} alt="hero" />
            <StyledBlueTriangle as="img" src={greyTriangle} alt="blue" />
            <TextWrapper>
                <StyledText>Welcome, my name is Micha≈Ç</StyledText>
                <Button>You should know me better</Button>
            </TextWrapper>
        </HeroPageWrapper>
    )
}

export {HeroPage};
