import React, {useRef, useEffect} from "react";
import styled, {ThemeProvider} from "styled-components";
import greyTriangle from "../misc/images/tr_2.png";
import heroImg from "../misc/images/hero2.png"
import PageWrapper from "../components/PageWrapper";
import StyledTriangle from "../components/Triangles.js";
import HeroImg from "../components/HeroImage";
import Button from "../components/Button";
import TextRectangle from "../components/TextRectangle";
import rootTheme from "../themes/root";
import ReactFullpage from "@fullpage/react-fullpage";
import {gsap} from 'gsap';
import {test} from "../App"

const HeroPageWrapper = styled(PageWrapper)`
  display: flex;
  justify-content: space-around;
  align-items: center;
`;

const TextWrapper = styled.div`
  display: flex;
  flex-flow: column;
  justify-content: center;
  font-size: ${props => props.theme.fontSize.xxlarge}vw;
  z-index: 10;
`;

const StyledText = styled.h1`
  z-index: 1;
  font-size: ${props => props.theme.fontSize.xxlarge}vw;
  font-weight: bold;
  margin: 0;
`;

const HeroButton = styled(Button)`
margin-top: 1vh;
width: 45%;
height: 10vh;
align-self: flex-end;
font-weight: bold;
font-size: ${props => props.theme.fontSize.buttonFontSize}vw;
color: ${props => props.theme.colors.textColor};
z-index: 100;
`;

const HeroPage = (props) => {
    const ref = useRef(null);

    useEffect(() => {
        if(test = 1)
        {

            const deref = ref.current.children;
            const img = deref[0];
            const text = deref[2];
            const btn = text.children[2];

            gsap.set([img, text, btn], {autoAlpha: 0});
            const tl = gsap.timeline({defaults: {ease: "ease-in"}});

            tl.fromTo(img, {y: '+=500'},{y: 0, duration: 1});
            tl.to(img, {autoAlpha: 1, duration: 1.5}, "-=.5");
            tl.fromTo(text,{x: '+=200'}, {x: 0, autoAlpha: 1, duration: 1}, '-=0.5');
            tl.fromTo(btn,{y: '-=50'}, {y: 0, autoAlpha: 1, duration: 1});
        }
    })
    return (
        <ThemeProvider theme={rootTheme}>
            <HeroPageWrapper  ref={ref} id={props.id} className="hero">
                <HeroImg as="img" src={heroImg} alt="hero" id={props.imgId}/>
                <StyledTriangle as="img" src={greyTriangle} alt="grey"/>
                <TextWrapper id={props.textWrapperId}>
                    <TextRectangle primary>Welcome,</TextRectangle>
                    <StyledText>my name is Micha≈Ç</StyledText>
                    <HeroButton primary onClick={() => props.api.moveSectionDown()} as="a" id={props.heroBtnId}>
                        You should know me better
                    </HeroButton>
                </TextWrapper>
            </HeroPageWrapper>
        </ThemeProvider>
    )
}

export {HeroPage};
