import React from "react";

const rootTheme = {
    colors: {
        mainBackground: "#242526",
        accentBackground: "#343439",
        blueAccent: "#39397f",
        textColor: "#dadce1"
    },
    fontSize: {
        small: 1,
        medium: 2,
        large: 3,
        xlarge: 4,
        xxlarge: 5,
        buttonFontSize: 1.25,
    },
    fontFamily: "Roboto, sans-serif",
};

export default rootTheme;
