import React, {useRef, useEffect, useState} from "react";
import styled, {ThemeProvider} from "styled-components";
import PageWrapper from "../components/PageWrapper";
import blueTriangle from "../misc/images/tr_3.png";
import StyledTriangle from "../components/Triangles";
import rootTheme from "../themes/root";
import Project from "../components/Project";
import {ReactComponent as Arrow} from "../misc/images/arrow.svg";
import laptopC from "../misc/images/lap 1.svg";
import laptopReact from "../misc/images/laptopReact.svg";
import {ReactComponent as Laptop} from "../misc/images/lap 1.svg";
import {gsap} from 'gsap';
import userEvent from "@testing-library/user-event";

const MyWorkPageWrapper = styled(PageWrapper)`
    display: flex;
    flex-flow: column;
`;

const CentringDiv = styled.div`
  display: flex;
  flex-flow: column;
  justify-content: space-between;
  height: 70vh;
`;

const TextWrapper = styled.div`
    margin-top: 5vh;
    font-size: ${props => props.theme.fontSize.xlarge}vw;
    font-weight: bold;
    justify-self: center;
    align-self: center;
    margin-bottom: 5vh;
`;

const ProjectsWrapper = styled.div`
    position: relative;
    width: 100%;
    height: 55vh;
    display: flex;
    justify-content: space-between;
    align-items: center;
    //position: ;
`;

const ArrowRight = styled(Arrow)`
    width: 10vw;
    height: 10vh;
    z-index: 1;
    
    &:hover {
    cursor: pointer;
    fill: red;
    }
`;

const ArrowLeft = styled(ArrowRight)`
    transform: rotate(180deg);
`;

const FirstProject = styled(Project)`
`;

const SecondProject = styled(Project)`
`;

const ThirdProject = styled(Project)`
`;

const StyledLaptop = styled(Laptop)`
  z-index: 1;
`

const Wrapper = styled.div`
position: relative;
width: 50%;
height: 100%;
`


const MyWorkPage = (props) => {
    const ref = useRef(null)

    const [renderCnt, setRenderCnt] = useState(0);

    const [projectsArr, setProjectsArr] = useState([]);

    const [currentProject, setCurrentProject] = useState(null);

    const [arrowArray, setArrowArray] = useState([])

    useEffect(() => {
        if(renderCnt === 0)
        {
            const ProjectsWrapper = ref.current.children;
            const tempArr = [];
            let i;
            for(i = 0; i < ProjectsWrapper.length; ++i)
                tempArr.push(ProjectsWrapper[i].children[0])
            setArrowArray([document.getElementById("arrowLeft"), document.getElementById("arrowRight")])
            const parti = document.getElementById("Particles");
            setProjectsArr(tempArr);
            setRenderCnt(1);
        }
    }, [renderCnt, ref])

    useEffect(() => {
        setCurrentProject({project: projectsArr[0], index: 0});
        gsap.set(projectsArr.slice(1), {autoAlpha: 0});
    }, [projectsArr])

    const animate = (project) => {
        const [header, text, button] = project.children;
        gsap.set([header, text, button], {autoAlpha: 0});
        const tl = gsap.timeline();
        tl.to([currentProject.project.children[0],currentProject.project.children[1],currentProject.project.children[2]]
            , {x: "+=100", autoAlpha: 0});
        tl.to(currentProject.project, {scaleY: 0.3, scaleX: 0.65, x: "-=90%", duration: 1.5});
        tl.to(currentProject.project, {autoAlpha: 0, duration: .5}, "-=0.85");
        tl.fromTo(project, {scaleY: 0.3, scaleX: 0.65, x: "-=90%"}, {scale: 1, x: 0, duration: 1.5})
        tl.to(project, {autoAlpha: 1, duration: .5}, "-=1.5");
        tl.fromTo(header,{x: "+=100"}, {x: 0, autoAlpha: 1})
        tl.fromTo(text,{x: "+=100"}, {x: 0, autoAlpha: 1})
        tl.fromTo(button,{x: "+=100"}, {x: 0, autoAlpha: 1})
    }

    const loadProjectRight = () => {
        //check if current page is not last in the array
        const nextIdx = currentProject.index + 1;
        if(nextIdx < projectsArr.length)
        {
            const nextProject = projectsArr[nextIdx];
            animate(nextProject);
            setCurrentProject({project: nextProject, index: nextIdx});
        }
    }

    const loadProjectLeft = () => {
        //check if current page is not last in the array
        const prevIdx = currentProject.index - 1;
        if(prevIdx >= 0)
        {
            const prevProject = projectsArr[prevIdx];
            animate(prevProject);
            setCurrentProject({project: prevProject, index: prevIdx});
        }
    }

    const checkArr = () => {
        return (currentProject.index !== (projectsArr.length - 1) && currentProject.index !== 0)
    }

    const arrowHover = (e, index) => {
        e.target.style.fill = '#a2ffd2';
        console.log(e.target === document.getElementById("arrowRight"))
        gsap.to(e.target, {scaleX: 1.1});
    }

    const arrowDishover = (e) => {
        e.target.style.fill = '#dadce1';
        gsap.to(e.target, {scaleX: 1})
    }

    return (
        <ThemeProvider theme={rootTheme}>
            <MyWorkPageWrapper id={props.id}>
                <CentringDiv>
                    <StyledTriangle as="img" src={blueTriangle} alt="blue"/>
                    <TextWrapper>Here you can find my projects</TextWrapper>
                    <ProjectsWrapper>
                        <ArrowLeft onMouseOver={arrowHover} onMouseOut={arrowDishover} onClick={loadProjectLeft} id={"arrowLeft"} />
                        <StyledLaptop width={"50%"}/>
                        <Wrapper ref={ref}>
                            <SecondProject src={laptopC} header={"Animations in FLTK!"} text={FirstProjectText}
                                           href={"https://github.com/Kalin8900/Studies/tree/master/zad_6"}/>
                            <FirstProject  src={laptopC} header={"EAN-8 generator"} text={FirstProjectText}
                                           href={"https://github.com/Kalin8900/Studies/tree/master/zad_7"}/>
                            <ThirdProject src={laptopReact} header={"My first React site"} text={FirstProjectText}
                                          href={"https://github.com/Kalin8900/Studies/tree/master/zad_6"}/>
                        </Wrapper>
                        <ArrowRight onMouseOver={arrowHover} onMouseOut={arrowDishover} onClick={loadProjectRight} id={"arrowRight"} />
                    </ProjectsWrapper>
                </CentringDiv>
            </MyWorkPageWrapper>
        </ThemeProvider>
    )
}

const FirstProjectText = "This project was about refactoring code from C to C++. I received a short C program which was just creating and saving the bitmap. The main goal was to create a bitmap with generated EAN-8 Code and save it to file. " +
    "I've done this by creating a class witch handle the bitmap creating and saving. The class has also a method which can generate a EAN-8 Code with (almost) any size of the image. The implementation of the class is very surprising. I really recommend to take a look on it.";

export {MyWorkPage};
