import React from "react";
import styled from "styled-components";

const rootTheme = {
    colors: {
        mainBackground: "#242526",
        accentBackground: "#343439",
        blueAccent: "#39397f",
        textColor: "#dadce1"
    },
    fontSize: {
        medium: 3
    }
};

export default rootTheme;
