import React, {useRef, useEffect} from 'react';
import {HeroPage} from "./pages/HeroPage"
import {AboutMePage} from "./pages/AboutMePage";
import {HireMePage} from "./pages/HireMePage";
import {MyWorkPage} from "./pages/MyWorkPage";
import styled from "styled-components";
import Button from "./components/Button";
import gsap from 'gsap';

function App() {
    const ref = useRef(null);
    useEffect(() => {
        const pages = ref.current.children;
        const heroPage = pages[0];
        const aboutMePage = pages[1];
        const myWorkPage = pages[2];

        // console.log(heroBg);

        const heroImg = heroPage.children[0];
        const heroText = heroPage.children[2];
        const heroBtn = heroText.children[2];

        gsap.set(heroPage, {autoAlpha: 0});

        const tl = gsap.timeline({defaults: {ease: 'ease-in'}});

        tl.to(heroImg, {autoAlpha: 1, duration: 1}, 3);
        tl.to(heroText, {autoAlpha: 1, duration: 1});
        tl.to(heroBtn, {autoAlpha: 1, duration: 1});
    })

    return (
        <div ref={ref}>
            <HeroPage id={"hero"} imgId={"heroImg"} textWrapperId={"heroText"} heroBtnId={"heroBtn"} link={"#aboutMe"}/>
            <AboutMePage id={"aboutMe"}/>
            <MyWorkPage id={"myWork"}/>
        </div>
    );
}

export default App;

