import React, {useRef, useEffect} from 'react';
import {HeroPage} from "./pages/HeroPage"
import {AboutMePage} from "./pages/AboutMePage";
import {HireMePage} from "./pages/HireMePage";
import {MyWorkPage} from "./pages/MyWorkPage";
import {gsap} from 'gsap';
import {ScrollTrigger} from "gsap/ScrollTrigger";
import ReactFullpage from "@fullpage/react-fullpage";


const animation = (pageIndex, page) => {
    const tl = gsap.timeline({defaults: {ease: 'power2'}});

    if(pageIndex === 0)
    {
        // if(page.length === 4)
        // {
            gsap.set([page[0], page[2], page[3], page[4]], {autoAlpha: 0});
            tl.to(page[0],{autoAlpha: 1, duration: 1}, 0.5);
            tl.fromTo(page[2],{x: '+=200'}, {x: 0, autoAlpha: 1, duration: 1}, '-=0.5');
            tl.fromTo(page[3],{y: '-=50'}, {y: 0, autoAlpha: 1, duration: .7});
            tl.fromTo(page[4], {y: '+=50'}, {y: 0, autoAlpha: 1, duration: .5})
        // }
        // else
        //     console.log("To animate first page exactly 4 elements are required. heroImg, heroText and heroBtn, exactly in that order");
    }
    else if(pageIndex === 1)
    {

        gsap.set([page[1], page[2], page[3]], {autoAlpha: 0});
        tl.fromTo(page[1], {x: '+=200'}, {x: 0, autoAlpha: 1, duration: 1});
        tl.fromTo(page[2], {x: '+=200'}, {x: 0, autoAlpha: 1, duration: 1}, "-=0.2");
        tl.fromTo(page[3], {x: '-=200'}, {x: 0, autoAlpha: 1, duration: 1}, "-=0.2");

    }
}

const Fullpage = React.forwardRef((props, ref) => (
    <ReactFullpage
        //fullpage options
        licenseKey = {'Q85^XEW#h3'}
        scrollingSpeed = {1000}
        navigation = {true}
        navigationTooltips ={["hero", "about me", "my work"]}
        responsiveWidth = {768}
        onLeave = {(origin, destination) => {
            const [cell] = destination.item.children;
            const [page] = cell.children;
            console.log(page);
            animation(destination.index, page.children);
        }}

        render={({ state, fullpageApi }) => {
            return (
                <ReactFullpage.Wrapper ref={ref}>
                    <div className="section">
                        <HeroPage api={fullpageApi} id={"hero"} imgId={"heroImg"} textWrapperId={"heroText"} heroBtnId={"heroBtn"} link={"#aboutMe"}/>
                    </div>
                    <div className="section">
                        <AboutMePage api={fullpageApi} id={"aboutMe"}/>
                    </div>
                    <div className="section">
                        <MyWorkPage className={"section"} id={"myWork"}/>
                    </div>
                </ReactFullpage.Wrapper>
            );
        }}
    />
))

function App() {
    const ref = useRef(null);
    useEffect(() => {
        const deref = ref.current.children;
        const fullPageWrapper = deref[0];
        const fullPageSections = fullPageWrapper.children;
        const [cell] = fullPageSections[0].children;
        const [heroPage] = cell.children;

        const heroImg = heroPage.children[0];
        const heroText = heroPage.children[2];
        const heroBtn = heroText.children[2];
        const heroArrowWrapper = heroPage.children[3];

        gsap.fromTo(heroImg, {y: '+=500'}, {y: 0, duration: 1});
        animation(0, heroPage);
        gsap.to(heroArrowWrapper.children[1], {y: "+=15", repeat: -1, duration: 0.7, yoyo: true, width: 2.65 + "vw"})
    })

    return (
        <div ref={ref}>
            <Fullpage/>
        </div>

    );
};

export {App, Fullpage};

//Icons made by <a href="https://www.flaticon.com/authors/smashicons" title="Smashicons">Smashicons</a> from <a href="https://www.flaticon.com/" title="Flaticon"> www.flaticon.com</a>
