import React from "react";
import styled from "styled-components";
import StyledTriangle from "./Triangles";

const ScatterWrapper = styled.div`
    display: flex;
    flex-flow: row wrap;
    flex-wrap: wrap;
    position: absolute;
    height: ${props => props.height}%;
    width: ${props => props.width}%;
    bottom: 0;
`;

const HalfSizeTriangle = styled(StyledTriangle)`
    position: unset;
    width: 50%;
    height: ${props => props.height};
`;

let Scatter = (props) => {
    return (
        <ScatterWrapper height={props.height} width={props.width}>
            <HalfSizeTriangle as="img" height={100} src={props.TriangleSource} />
            <HalfSizeTriangle as="img" height={100} src={props.TriangleSource} />
            <HalfSizeTriangle as="img" height={100} src={props.TriangleSource} />
            <HalfSizeTriangle as="img" height={100} src={props.TriangleSource} />
        </ScatterWrapper>
    )
}

export default Scatter;
