import React from "react";
import styled from "styled-components";
import gitLogo from "../misc/images/github.png";
import scatterPng from "../misc/images/Scatter_Main.png";
import Button from "./Button";
import {Scatter} from "./Scatter";
import {ReactComponent as LaptopC} from "../misc/images/laptopC++.svg";

const ProjectWrapper = styled.div`
    width: 75vw;
    height: 45vh;
    background-color: ${props => props.theme.colors.accentBackground};
    z-index: 1;
    display: flex;
    flex-flow: row;
    justify-content: center;
    align-items: center;
    position: relative;
    box-shadow: 2px 2px 2px 1px #181b21;
`;

const ImageWrapper = styled.div`
  width: 50%;
  height: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const TextWrapper = styled.div`
  width: 50%;
  height: 100%;
  display: flex;
  flex-flow: column;
  justify-content: space-evenly;
  align-items: center;
`;

const StyledLaptop = styled(LaptopC)`
    z-index: 3;
`;

const StyledScatter = styled(Scatter)`
  position: absolute;
  left: 0;
  z-index: 0;
`;

const LinksWrapper = styled.div`
    //margin-top: 2vh;
    height: 25%;
    width: 100%;
    display: flex;
    font-size: ${props => props.theme.fontSize.medium}vh;
    font-weight: bold;
    justify-content: space-evenly;
    align-items: center;
    text-decoration: none;
    color: ${props => props.theme.colors.textColor};
`;

const LinkLogo = styled.img`
    height: 65%;
    //margin-right: 1vw;
`;

const TextSpan = styled.div`
    margin: 0 15%;
    text-align: center;
`;

const Header = styled.div`
    font-size: ${props => props.theme.fontSize.large}vw;
    font-weight: bold;
    //margin: 1vh 0;
    text-align: center;
`;

const Project = (props) => {

    return (
        <ProjectWrapper>
            <StyledScatter ScatterSource={scatterPng} height={50} width={15}/>
            <ImageWrapper>
                <StyledLaptop width={"100%"} />
            </ImageWrapper>
            <TextWrapper>
                <Header>{props.header}</Header>
                <TextSpan>
                    {props.text}
                </TextSpan>
                <LinksWrapper as="a" href={props.href}>
                    <LinkLogo src={gitLogo} alt={"Git"}/>
                    <Button primary>Check the source!</Button>
                </LinksWrapper>
            </TextWrapper>
        </ProjectWrapper>
    )
};



